mapper.select:
SELECT DISTINCT t3.ID_ as id, t1.ID_ AS taskId, t1.NAME_ AS taskName, t4.NAME_ AS formName, t2.TENANT_ID_ AS tenantId, t4.BUSINESS_KEY_ AS businessKey, t2.PROC_INST_ID_ AS processInstanceId, t1.CREATE_TIME_ AS startTime, t3.CUSTOM_TASK_MAX_DAY_ as customTaskMaxDay, t3.TASK_MAX_DAY_ as taskMaxDay, t1.TASK_DEF_KEY_ as taskDefinitionKey, t1.PROC_DEF_ID_ as processDefinitionId, t5.DISPLAY_NAME_ as realName,
if(t3.SUSPENSION_STATE_=2 && t3.OVER_TIME = 2, date_add(t3.END_TIME_,interval TIMESTAMPDIFF(SECOND, t3.SUSPENSION_TIME_, NOW()) SECOND),t3.END_TIME_ ) as endTime, t3.WORK_END_TIME_ as workEndTime, t1.ASSIGNEE_ as assignee, t1.owner_ as owner,
if(t3.SUSPENSION_STATE_=2 && t3.OVER_TIME = 2, TIMESTAMPDIFF(SECOND,t3.SUSPENSION_TIME_,t3.END_TIME_) ,TIMESTAMPDIFF(SECOND,CURRENT_TIMESTAMP(),t3.END_TIME_)) as restTime,
if(t3.SUSPENSION_STATE_=1, TIMESTAMPDIFF(SECOND,t1.CREATE_TIME_,CURRENT_TIMESTAMP()), TIMESTAMPDIFF(SECOND,t1.CREATE_TIME_,t3.SUSPENSION_TIME_)) as duration, t3.SUSPENSION_TIME_ as suspensionTime, case t3.SUSPENSION_STATE_ when 1 then '激活' else '挂起' end as suspensionState, case t3.OVER_TIME when 1 then '正常' else '延长' end as overtime, t3.GROUP_ID_ as groupId, t3.FLOW_TYPE_ as flowType, t3.FROM_KEY_ as fromKey, t3.IS_DB_ as isDb, t3.BUSINESS_INFO_ as businessInfo, '待办' as 'status' FROM act_ru_task t1 LEFT JOIN act_ru_execution t2 ON t1.PROC_INST_ID_ = t2.PROC_INST_ID_ and t1.EXECUTION_ID_ = t2.ID_ LEFT JOIN act_ru_extension_task t3 ON T1.ID_ = t3.TASK_ID_ and t1.PROC_INST_ID_ = t3.PROC_INST_ID_ LEFT JOIN act_hi_procinst t4 ON T1.PROC_INST_ID_ = t4.PROC_INST_ID_ LEFT JOIN act_id_user t5 ON t1.ASSIGNEE_ = t5.ID_ WHERE t3.ID_ is not null
and t1.PROC_INST_ID_ = #{processInstanceId}

mapper.update:
UPDATE  act_ru_extension_task t1
SET CUSTOM_TASK_MAX_DAY_ = #{customTaskMaxDay},
    END_TIME_ = #{endTime},
    TASK_REST_TIME_= #{restTime}
WHERE
    t1.ID_ = #{id}

UPDATE  act_ru_extension_task t1
SET ASSIGNEE_ = #{assignee},business_info_ = #{variables}
WHERE
    t1.PROC_INST_ID_ = #{processInstanceId} and t1.TASK_ID_ = #{taskId}

UPDATE  act_ru_extension_task t1
SET IS_DB_ = #{isDb}
WHERE
    t1.PROC_INST_ID_ = #{processInstanceId} and t1.TASK_ID_ = #{taskId}
